import React from "react";

const callToApi = () => {
  return fetch("https://api.tvmaze.com/search/shows?q=paranormal")
    .then((response) => response.json())
    .then((response) => {
      const result = {
        name: response.show.name,
        id: response.show.id,
        language: response.show.language,
      };
      return result;
    });
};

export default callToApi;
