    setIsOpen(!isOpen);
  };
  const renderMenu = () => {
    if (isOpen === true) {
      return (
        <div className="menu__container">
          <box-icon
            name="right-arrow-alt"
            className="bx"
            onClick={handleMenu}
          ></box-icon>
          <a href="#" className="menu__link">
            Â¿Hablamos?
          </a>
