          <a href="#" className="menu__link">
            Sobre nosotros
          </a>
          <a href="#" className="menu__link">
            Contacto
          </a>
        </div>
      );
    } else {
      return (
        <div onClick={handleMenu}>
          <box-icon name="menu" className="bx"></box-icon>
        </div>
      );
    }
  };
  // const [open, setOpen] = useState("hidden");

